filterUdt.filterCondition:
data | @ | {
    qtdAcordosTotal: length(@),
    qtdAcordosElegiveis: length(@[?tipo_renegociacao != 'RPS' && meio_pagamento_acordo != 'D']),
    acordos: @,
    acordosElegiveis: @[?tipo_renegociacao != 'RPS' && meio_pagamento_acordo != 'D']
}

protocols | @ | {
    qtdDocumentosTotal: length(@),
    qtdDocumentosElegiveis: length(@[?statusBoleto == 'Ativo' || statusBoleto == 'Vencido']),
    documentos: @,
    documentosElegiveis: @[?statusBoleto == 'Ativo' || statusBoleto == 'Vencido']
}

