filterUdt.filterCondition:
{
   cedidos: data.contratos[?indicador_bloqueio_renegociacao == `true` && ofertas[?motivo_bloqueio_oferta == 'cedido']] | @ | {
        count: length(@[]),
        nome_primeira_oferta: @[0].ofertas[?motivo_bloqueio_oferta == 'cedido'] | [0].descricao_restricao,
        contratos: @[]
    },
    itau: data.contratos[?qtd_parcelas_atraso != `0` && indicador_bloqueio_renegociacao == `false` && ofertas[?tipo_oferta == 'A' && indicador_bloqueio_oferta == `false`]] | @ | {
        count: length(@[]),
        contratos: @[]
    }
}

